import React, {useState} from "react";
import { GoChevronDown } from 'react-icons/go'

// items: Array<{ id: string; label: string; content: React.ReactNode }>

const AccordionFancy = ({ items }) => {
  // ✅ 老师的命名
  const [expandedIndex, setExpandedIndex] = useState(-1)

  // ✅ 老师的 if/else + 函数式更新
  const handleClick = (nextIndex) => {
    setExpandedIndex((currentExpandedIndex) => {
      if (currentExpandedIndex === nextIndex) {
        return -1
      } else {
        return nextIndex
      }
    })
  }

  return (
    <div className="space-y-3">
      {items.map((item, index) => {
        const open = index === expandedIndex

        // 渐变色：优先用数据里提供的 gradient；否则使用默认青到蓝
        const gradientClasses =
          item.gradient ??
          'from-cyan-500 to-blue-500 hover:from-cyan-600 hover:to-blue-600'

        return (
          <div key={item.id} className="w-full">
            {/* 头部：两层法做“渐变边框 + hover 渐变填充” */}
            <div
              className={`
                group relative inline-flex w-full items-stretch justify-between
                p-0.5 rounded-xl overflow-hidden
                bg-gradient-to-r ${gradientClasses}
                transition-colors duration-200
              `}
            >
              <button
                type="button"
                aria-expanded={open}
                aria-controls={`acc-content-${item.id}`}
                onClick={() => handleClick(index)}  // ✅ 和老师一致：传 index
                className="
                  relative flex w-full items-center justify-between gap-3 rounded-[10px]
                  bg-white dark:bg-gray-900 px-5 py-3
                  transition-all duration-200 ease-in
                  group-hover:bg-opacity-0
                "
              >
                <span
                  className="
                    font-semibold text-gray-900 dark:text-gray-100
                    transition-colors group-hover:text-white
                  "
                >
                  {item.label}
                </span>

                {/* 一个图标 + 旋转，等价于上下两个图标的切换 */}
                <GoChevronDown
                  className={`
                    shrink-0 transition-transform duration-200
                    ${open ? 'rotate-180 text-white' : 'rotate-0 group-hover:text-white'}
                  `}
                  size={20}
                />
              </button>
            </div>

            {/* 内容：常驻 + CSS 过渡（max-height / opacity），不做 DOM 显隐 */}
            <div
              id={`acc-content-${item.id}`}
              className={`
                overflow-hidden rounded-xl border border-gray-200 dark:border-gray-700
                bg-white dark:bg-gray-900
                transition-all duration-300 ease-in-out
                ${open ? 'max-h-96 opacity-100 mt-2 p-4' : 'max-h-0 opacity-0 mt-0 p-0'}
              `}
            >
              <div className="text-sm text-gray-700 dark:text-gray-300">
                {item.content}
              </div>
            </div>
          </div>
        )
      })}
    </div>
  )
}

export default AccordionFancy
